import{WebComponentService}from"./WebComponentService.min.js";window.customElements.define("animation-service",class extends WebComponentService{onLoad(){let e=document.querySelectorAll("[data-animate]");if(e=Array.from(e).filter((e=>!e.classList.contains(e.getAttribute("data-animate")))),0===e.length)return;const t=new IntersectionObserver(((e,t)=>{for(const n of e)if(n.isIntersecting){const e=n.target.getAttribute("data-animate");n.target.classList.add(e),t.unobserve(n.target)}}));for(const n of e)t.observe(n)}});